import { useState, useRef } from 'react';
import styled from 'styled-components';
import Arrow from './arrow';
import ImgSlider from './imgSlider';
import { FaArrowCircleLeft, FaArrowCircleRight } from 'react-icons/fa';

const Styled = styled.section`
  position: relative;
  width: 100%;
  height: 300px;
  margin: 5em 0;

  .carousel--wrapper {
    height: inherit;
    width: 100%;
    display: flex;
    /* overflow-x: auto; */
    -webkit-overflow-scrolling: touch;
    scroll-snap-points-x: repeat(300px);
    scroll-snap-type: mandatory;
    transform: scale(0.98);
  }

  .active {
    transform: scale(1);
  }

  .img--slider {
    flex-shrink: 0;
    width: 300px;
  }

  .slide-arrow {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    color: var(--white-clr);
    font-size: 2rem;
    margin: 0 0.2em;
    transition: var(--ease--in--out--02s);

    &:hover,
    &:focus {
      color: var(--second-clr);
    }
  }

  .right {
    right: 0;
  }
`;

const Carousel = () => {
  const [active, setActive] = useState(false);
  const [startX, setStartX] = useState();
  const [scrollLeft, setScrollLeft] = useState();

  const carouselRef = useRef();
  const imgs = {
    0: '/burger.jpg',
    1: '/craft_beer.jpg',
    2: '/drink_pink.jpg',
    3: '/SimonesBarFaceS.jpg',
    4: '/PinballBooth.jpg',
    5: '/pizza.jpg',
    6: '/patio.jpg',
    7: '/santi.jpg'
  };

  const handleMouseDown = e => {
    console.log(e.pageX);
    setActive(true);
    setStartX(e.pageX - carouselRef.current.offsetLeft);
    console.log('pp', startX);
  };
  const handleMouseOff = e => {
    console.log(e.target);
  };

  const handleMouseOn = e => {
    console.log(e.target);
  };

  return (
    <Styled className='carousel'>
      <Arrow direction='left' glyph={<FaArrowCircleLeft />} />
      <Arrow direction='right' glyph={<FaArrowCircleRight />} />
      <div
        ref={carouselRef}
        className={`carousel--wrapper ${active && 'active'}`}
        onMouseDown={handleMouseDown}
        // onMouseLeave={handleMouse}
        // onMouseUp={handleMouse}
        // onMouseMove={handleMouse}
      >
        {Object.values(imgs).map((img, i) => (
          <ImgSlider className='img--slider' key={i} imgUrl={img} />
        ))}
      </div>
    </Styled>
  );
};

export default Carousel;
