import { useState } from 'react';
import styled from 'styled-components';
import Button from './ui/button';
import TitleBanner from './ui/titleBanner';
import TabStyled from './ui/tabs';

const Styled = styled.section`
  text-align: center;
`;

const Contact = () => {
  const [form, setForm] = useState({
    name,
    email,
    phone,
    subject,
    message
  });

  const { name, email, phone, subject, message } = form;

  const handleChange = ({ target }) =>
    setForm(form => ({ ...form, [target.name]: target.value }));

  const handleSubmit = e => {
    e.preventDefault();
    console.log(form);
  };

  return (
    <Styled className='contact section'>
      <TitleBanner
        topTitle='GET IN TOUCH'
        title='CONTACT US'
        botTitle='SHOOT US A QUESTION'
      />
      <p className='phone'>(312) 666-8601</p>
      <p className='email'>info@simonesbar.com</p>
      <form
        className='form'
        name='contact'
        method='POST'
        data-netlify-recaptcha='true'
        data-netlify='true'
        onSubmit={handleSubmit}
      >
        <input
          onChange={handleChange}
          value={name}
          name='name'
          type='text'
          className='input--link name'
          placeholder='FULL NAME'
        />
        <input
          onChange={handleChange}
          value={email}
          name='email'
          type='text'
          className='input--link email--form'
          placeholder='EMAIL'
        />
        <input
          onChange={handleChange}
          value={phone}
          name='phone'
          type='text'
          className='input--link phone'
          placeholder='PHONE NUMBER'
        />
        <select
          onChange={handleChange}
          value={subject}
          name='subject'
          className='input--link subject'
          placeholder='SUBJECT'
        >
          <option>Select a subject...</option>
          <option>General Inquiries</option>
          <option>Music Bookings</option>
          <option>Special Events</option>
        </select>
        <textarea
          onChange={handleChange}
          value={message}
          name='message'
          cols='30'
          rows='10'
        ></textarea>
        <TabStyled className='form--button'>send message</TabStyled>
      </form>
    </Styled>
  );
};

export default Contact;
