import styled from 'styled-components';
import Button from './button';

const Styled = styled.div`
  position: relative;
  background: url('/outisde.jpg');
  background-repeat: no-repeat;
  background-position: center;
  background-attachment: scroll;
  background-size: cover;
  width: 100%;
  max-width: 500px;
  height: 400px;
  text-align: center;
 z-index: 1;

&::before {
  content: '';
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  background-color: rgba(0, 0, 0, 0.85);
  z-index: -1;
}


  .img--banner__img {
    background: url('/box.png');
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-position: center;
    background-size: 100% 100%;
}
  }
`;

const ImgBanner = ({ img, title, subtile, btnTitle, toLink }) => (
  <Styled className='img--banner'>
    <div className='img--banner__img' />
    <h2 className='title'>{title}</h2>
    <h3 className='subtitle'>{subtile}</h3>
    <Button toLink={toLink} title={btnTitle} />
  </Styled>
);

export default ImgBanner;
