import React, { useState } from 'react';
import styled from 'styled-components';
import TitleBanner from '../ui/titleBanner';
import Button from '../ui/button';

const Styled = styled.section`
  background-color: var(--primary-clr);

  /* title--banner */
  .title--banner {
    .title {
      color: var(--white-clr);
      -webkit-text-stroke: 2px var(--white-clr);
    }
  }
`;

const HomeMenu = () => {
  return (
    <Styled className='home--menu'>
      <TitleBanner
        topTitle='CHECK IT OUT'
        title='menu'
        botTitle='come eat some good food'
      />
      <div className='taps--container'>
        <button className='tab'>drinks</button>
        <button className='tab'>beer</button>
        <button className='tab'>food</button>
      </div>
      <div className='menu'>
        <div className='menu--item'>
          <div className='menu--item--text'>
            <h3 className='title'></h3>
            <p className='para'></p>
          </div>
          <p className='menu--item--money'></p>
        </div>
      </div>
      <Button toLink='/menu' title='view more' bgclr />
      <div className='menu--img'>
        <img src='/menu_img.png' alt='nacho beer image' />
      </div>
    </Styled>
  );
};

export default HomeMenu;
