import { useState, useRef, useEffect } from 'react';
import styled from 'styled-components';
import { FaPhone, FaMapMarkerAlt } from 'react-icons/fa';
import Link from 'next/link';
import Socials from './ui/socials';

const Styled = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  z-index: 1000;

  &::before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    height: 100vh;
    width: 100vw;
    background-color: rgba(0, 0, 0, 0.6);
    display: ${props => (props.toggle ? 'block' : ' none')};
    opacity: ${props => (props.toggle ? 1 : 0)};
    z-index: ${props => (props.toggle ? '' : -3)};
  }

  /* nav links */
  .nav--link {
    &__item {
      font-weight: var(--fw-bold);
      color: var(--second-clr);
      font-size: 1.1rem;
      transition: var(--ease--in--out--02s);
      cursor: pointer;

      &:hover,
      &:focus {
        color: var(--primary-clr);
      }
    }
  }

  /* Top head */
  .head--top {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0 0.5em;

    .info {
      display: flex;
      flex-direction: column;
      align-items: flex-start;

      &--link {
        color: var(--white-clr);
        display: flex;
        justify-content: space-evenly;
        align-items: baseline;
        font-size: 0.85rem;
        width: max-content;
        cursor: pointer;

        svg {
          margin-right: 0.5em;
        }
      }
    }
  }

  .brand-logo--top {
    display: none;
  }

  /* NavBar */
  .navbar {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-top: 1em;
  }

  .brand-logo {
    display: block;
    width: 200px;
  }

  .nav--toggle {
    transition: var(--tran--opa);
    opacity: ${props => (props.toggle ? '0' : 1)};
    margin-right: 1em;
  }

  .burger,
  .burger::before,
  .burger::after {
    background-color: ${props =>
      props.toggle ? 'var(--second-clr)' : 'var(--white-clr)'};
    width: 1.5em;
    height: 3px;
    transition: var(--tran--opa);
  }

  .burger {
    display: block;
    position: relative;
    transform: ${props => (props.toggle ? 'rotate(45deg)' : '')};

    &:hover,
    &:focus,
    &:hover::before,
    &:focus::before,
    &:hover::after,
    &:focus::after {
      background-color: var(--second-clr);
    }

    &::after,
    &::before {
      content: '';
      position: absolute;
      left: 0;
    }

    &::before {
      bottom: 6px;
      opacity: ${props => (props.toggle ? '0' : '1')};
    }
    &::after {
      top: 6px;
      transform: ${props =>
        props.toggle ? 'rotate(90deg) translate(-6px)' : ''};
    }
  }

  .inside-nav {
    opacity: 1;
    margin-right: 0;
  }

  .info--nav {
    display: none;
  }
  /* navbar--group */
  .navbar--group {
    z-index: 10;
    transition: var(--tran--opa);
    position: fixed;
    top: 0;
    right: 0;
    background-color: var(--white-clr);
    height: 100vh;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-evenly;
    width: 70vw;
    transform: ${props =>
      props.toggle ? 'translateX(0)' : 'translateX(105%)'};
  }

  @media screen and (min-width: 760px) {
    /* Top head */
    .head--top {
      display: flex;
      justify-content: space-between;
      align-items: center;
      padding: 0 0.5em;

      .info {
        display: flex;
        flex-direction: row;
        align-items: flex-start;
        width: 50%;
        justify-content: space-between;
      }
    }

    .brand-logo {
      width: 250px;
    }
  }

  @media screen and (min-width: 1024px) {
    position: absolute;
    margin-top: 1em;
    display: grid;
    justify-content: center;

    .brand-logo,
    .nav--toggle {
      display: none;
    }

    .brand-logo--top {
      display: block;
      border-right: 1px solid var(--white-clr);
      border-left: 1px solid var(--white-clr);
      padding: 0 3em;
    }

    /* Top head */
    .head--top {
      display: flex;
      justify-content: space-between;
      align-items: center;
      padding: 0;
      justify-self: center;
      width: 1000px;

      .info {
        flex-direction: column;
        width: auto;
      }
    }

    .navbar {
      border-top: 1px solid var(--white-clr);
      padding: 1em 4em 0;
    }

    /* Navbar */
    .navbar--group {
      z-index: 10;
      transition: var(--tran--opa);
      position: relative;
      top: 0;
      right: 0;
      background-color: transparent;
      height: auto;
      display: flex;
      flex-direction: row;
      align-items: center;
      justify-content: space-evenly;
      width: 1000px;
      transform: translateX(0);
    }

    .nav--link {
      &__item {
        color: var(--white-clr);
        &:hover,
        &:focus {
          color: var(--accent-clr);
        }
      }
    }
  }
`;

const Navbar = () => {
  const [toggle, setToggle] = useState(false);
  const [navScroll, setNavScroll] = useState(false);
  const navRef = useRef();

  useEffect(() => {
    window.addEventListener('scroll', () => {
      console.log(window.scrollY);
      console.log(navRef.current.scrollHeight);
      if (window.scrollY <= navRef.current.scrollHeight) {
        setNavScroll(true);
      }
    });
  }, [navRef]);

  console.log(navRef);
  return (
    <Styled ref={navRef} toggle={toggle} className='navbar'>
      <div className='head--top'>
        <Socials />
        <Link href='/'>
          <a className='brand-logo--top'>
            <img src='/logo.png' alt='logo' />
          </a>
        </Link>
        <div className='info'>
          <a className='info--link' href='tel:+312668601'>
            <FaPhone />
            (312) 666-8601
          </a>
          <a
            className='info--link'
            href={`https://www.google.com/maps/place/Simone's/@41.8581996,-87.6532561,17z/data=!3m1!4b1!4m5!3m4!1s0x880e2cf8f9586635:0x14d929f2b6402c66!8m2!3d41.8581996!4d-87.6510674`}
            target='_blank'
          >
            <FaMapMarkerAlt />
            960 W 18th, Chicago, IL 60608
          </a>
        </div>
      </div>

      <nav className='navbar'>
        <Link href='/'>
          <a className='brand-logo '>
            <img src='/logo.png' alt='logo' />
          </a>
        </Link>
        <div className='nav--toggle'>
          <span
            className='burger toggle'
            onClick={() => setToggle(!toggle)}
          ></span>
        </div>
        <ul className='navbar--group'>
          <div className='nav--toggle inside-nav'>
            <span
              className='burger toggle'
              onClick={() => setToggle(!toggle)}
            ></span>
          </div>
          <li className='nav--link'>
            <Link href='/'>
              <a className='nav--link__item'> HOME</a>
            </Link>
          </li>
          <li className='nav--link'>
            <Link href='/about'>
              <a className='nav--link__item'>ABOUT</a>
            </Link>
          </li>
          <li className='nav--link'>
            <Link href='/menu'>
              <a className='nav--link__item'>MENU</a>
            </Link>
          </li>
          <li className='nav--link'>
            <Link href='/privateEvents'>
              <a className='nav--link__item'>PRIVATE EVENTS</a>
            </Link>
          </li>
          <li className='nav--link'>
            <Link href='/photoGallery'>
              <a className='nav--link__item'>PHOTO GALLERY</a>
            </Link>
          </li>
          <li className='nav--link'>
            <Link href='/press'>
              <a className='nav--link__item'>PRESS</a>
            </Link>
          </li>
          <li className='nav--link'>
            <Link href='/contact'>
              <a className='nav--link__item'>CONTACT</a>
            </Link>
          </li>
        </ul>
      </nav>
    </Styled>
  );
};

export default Navbar;

