import styled from 'styled-components';

const Icon = styled.a`
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--second-clr);
  svg {
    margin-right: 0.5em;
    color: var(--primary-clr);
    font-size: 1.7rem;
  }
`;
export default Icon;
