import { useState, useRef, useEffect } from 'react';
import styled from 'styled-components';
import Arrow from './arrow';
import ImgSlider from './imgSlider';
import { FaArrowCircleLeft, FaArrowCircleRight } from 'react-icons/fa';

const Styled = styled.section`
  position: relative;
  width: 100%;
  height: 300px;
  margin: 5em 0;
  display: flex;
  /* overflow-x: auto; */
  -webkit-overflow-scrolling: touch;
  scroll-snap-points-x: repeat(300px);
  scroll-snap-type: mandatory;
  transform: scale(0.98);

  .active {
    /* transform: scale(1); */
  }

  .img--slider {
    flex-shrink: 0;
    width: 300px;
  }

  .slide-arrow {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    color: var(--white-clr);
    font-size: 2rem;
    margin: 0 0.2em;
    transition: var(--ease--in--out--02s);

    &:hover,
    &:focus {
      color: var(--second-clr);
    }
  }

  .right {
    right: 0;
  }
`;

const Carousel = () => {
  const [active, setActive] = useState(false);
  const [startX, setStartX] = useState();
  const [scrollLeft, setScrollLeft] = useState();

  const carouselRef = useRef();
  const imgs = {
    0: '/burger.jpg',
    1: '/craft_beer.jpg',
    2: '/drink_pink.jpg',
    3: '/SimonesBarFaceS.jpg',
    4: '/PinballBooth.jpg',
    5: '/pizza.jpg',
    6: '/patio.jpg',
    7: '/santi.jpg'
  };

  const handleMouseDown = e => {
    setActive(true);
    setStartX(e.pageX - carouselRef.current.offsetLeft);
    setScrollLeft(carouselRef.current.scrollLeft);
  };
  const handleMouseOff = () => setActive(false);

  const handleMouseMove = e => {
    if (!active) return;
    const x =
      e.pageX - carouselRef.current.offsetLeft &&
      carouselRef.current.offsetLeft;
    const walk = (x - startX) * 3;
    carouselRef.current.scrollLeft = scrollLeft - walk;
  };

  console.log(active);
  console.log(carouselRef);

  return (
    <Styled
      className='carousel'
      ref={carouselRef}
      onMouseMove={handleMouseMove}
      //       onMouseDown={handleMouseDown}
      // onMouseLeave={handleMouseOff}
      // onMouseUp={handleMouseOff}
    >
      <Arrow direction='left' glyph={<FaArrowCircleLeft />} />
      <Arrow direction='right' glyph={<FaArrowCircleRight />} />
      {Object.values(imgs).map((img, i) => (
        <ImgSlider className='img--slider' key={i} imgUrl={img} />
      ))}
    </Styled>
  );
};

export default Carousel;
