import React from 'react';
import styled from 'styled-components';
// import heroImg from "../images/bar_back.jpg"

const Styled = styled.div`
  position: relative;
  background-image: url(${props => props.img});
  background-position: center;
  background-size: cover;
  object-fit: cover;
  min-height: ${props => (props.heroSize ? '100vh' : 'calc(100vh - 40vh)')};
  text-align: center;
  display: -webkit-flex;
  display: -ms-flexbox;
  display: flex;
  -webkit-align-items: center;
  -ms-flex-align: center;
  align-items: center;
  -webkit-justify-content: center;
  -ms-flex-pack: center;
  justify-content: center;
  z-index: 1;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background-color: ${props => props.heroOpacity || 'rgba(0, 0, 0, 0.65)'};
    z-index: -1;
  }
`;

const hero = ({ img = '/bar_back.jpg', heroSize, heroOpacity, children }) => {
  return (
    <Styled
      className='hero'
      img={img}
      heroSize={heroSize}
      heroOpacity={heroOpacity}
    >
      {children}
    </Styled>
  );
};

export default hero;
