import styled from 'styled-components';
import Hero from '../components/ui/hero';
import Navbar from './navbar';
import '../global.css';
import Banner from './ui/banner';

const Styled = styled.div``;

const Layout = ({ children, heroSize, heroImg, bannerTitle, banner }) => {
  return (
    <>
      <Hero heroSize={heroSize} img={heroImg}>
        <Navbar />
        <Banner title={bannerTitle} banner={banner} />
      </Hero>
      {children}
    </>
  );
};

export default Layout;
