import { useState } from 'react';
import StateContext from './stateContext';

const State = ({ children }) => {
  const [tab, setTab] = useState('drinks');

  return (
    <StateContext.Provider
      value={{
        tab,
        setTab
      }}
    >
      {children}
    </StateContext.Provider>
  );
};

export default State;
