// import styled from 'styled-components';
import Head from 'next/head';
import Navbar from './navbar';
import Hero from '../components/ui/hero';
import Banner from './ui/banner';
import '../global.css';

const Layout = ({ children, heroSize, heroImg, bannerTitle, banner }) => {
  return (
    <>
      <Head>
        <title>Simone's Bar</title>
        <link href='../public/global.css' rel='stylesheet' />
        <link
          href='https://fonts.googleapis.com/css?family=Montserrat:400,500,700|Reenie+Beanie&display=swap'
          rel='stylesheet'
        />
      </Head>
      <Hero heroSize={heroSize} img={heroImg}>
        <Navbar />
        <Banner title={bannerTitle} banner={banner} />
      </Hero>
      {children}
    </>
  );
};

export default Layout;
