import styled from 'styled-components';
import { FaSearch } from 'react-icons/fa';

const Styled = styled.section`
  max-width: 1400px;
  margin: 3em 1em 5em;
  text-align: center;
  display: grid;
  gap: 1em;

  .title {
    color: var(--primary-clr);
    /* margin-bottom: 0.5em; */
  }

  .para {
    margin: 0.5em 1em;
    max-width: 600px;
  }

  .box {
    display: grid;
    gap: 1em;
    grid-template-columns: repeat(auto-fit, minmax(260px, 1fr));
    justify-items: center;
    align-items: center;
  }

  @media screen and (min-width: 1300px) {
    margin: 3em auto 5em;

    .box {
      grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
    }
  }
`;

const ImgBox = styled.div`
  position: relative;
  background: url(${props => props.img});
  background-position: center;
  background-size: cover;
  object-fit: cover;
  height: 220px;
  width: 100%;
  /* z-index: 0; */
  transition: var(--ease--in--out--02s);

  &::before {
    content: '';
    position: absolute;
    transition: var(--ease--in--out--02s);
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    background-color: rgba(0, 0, 0, 0.7);
    height: 100%;
    /* z-index: -1; */
  }

  .icon {
    transition: var(--ease--in--out--02s);
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    height: 50px;
    width: 50px;
    border-radius: 50%;
    background-color: #5d5d5d;
    color: var(--white-clr);
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;

    &:hover,
    &:focus {
      opacity: 0.85;
    }
  }

  @media screen and (min-width: 1200px) {
    &::before {
      height: 0;
      background-color: rgba(0, 0, 0, 0);
    }

    .icon {
      transform: translate(-50%, -50%) scale(0);
    }

    &:hover::before {
      background-color: rgba(0, 0, 0, 0.7);
      height: 100%;
    }

    &:hover .icon {
      transform: translate(-50%, -50%) scale(1);
    }
  }
`;

const PressContent = () => {
  const imgs = {
    0: '/outisde.jpg',
    1: '/FrontBar.jpg',
    2: '/PinballBooth.jpg',
    3: '/Column.jpg',
    5: '/bar_back.jpg',
    6: '/RoofPanel.jpg'
  };

  return (
    <Styled>
      <h2 className='title'>ALLWAYS WELCOME</h2>
      <p className='para'>
        Simone’s welcomes press and other inquiries for articles, video news
        reports, blog write-ups and other mentions in the media. Feel free to
        use our Web site as a way to get general information about us. To
        arrange an interview or get additional information, contact
        info@Simonesbar.com
      </p>
      <p className='para'>
        The following links point toward high-resolution JPG image files which
        may be used for media coverage about Simone’s. Please contact us
        regarding any usage questions.
      </p>
      <div className='box'>
        {Object.values(imgs).map((img, i) => (
          <ImgBox key={i} className='img-box' img={img}>
            <a className='icon' href={img} target='_blank'>
              <FaSearch />
            </a>
          </ImgBox>
        ))}
      </div>
    </Styled>
  );
};

export default PressContent;
