import { useState } from 'react';
import styled from 'styled-components';
import { FaPhone, FaMapMarkerAlt } from 'react-icons/fa';
// import logo from "../images/logo.png"
import Link from 'next/link';
import Socials from './ui/socials';

const Styled = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;

  /* nav links */
  .nav--link {
    &__item {
      font-weight: var(--fw-bold);
      color: var(--second-clr);
      font-size: 1.1rem;
      transition: var(--ease--in--out--02s);
      &:hover,
      &:focus {
        color: var(--primary-clr);
      }
    }
  }

  /* Top head */
  .head--top {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0 0.5em;

    .info {
      display: flex;
      flex-direction: column;
      align-items: flex-start;

      &--link {
        color: var(--white-clr);
        display: flex;
        justify-content: space-evenly;
        align-items: baseline;
        font-size: 0.85rem;
        width: max-content;

        svg {
          margin-right: 0.5em;
        }
      }
    }
  }

  /* NavBar */
  .navbar {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-top: 1em;
  }

  .brand-logo {
    display: block;
    width: 270px;
  }

  .burger,
  .burger::before,
  .burger::after {
    background-color: var(--white-clr);
    width: 1.5em;
    height: 3px;
    transition: transform 350ms ease-in-out, opacity 200ms linear;
  }

  .burger {
    position: relative;
    margin-right: 1em;
    z-index: 100;

    &:hover,
    &:focus,
    &:hover::before,
    &:focus::before,
    &:hover::after,
    &:focus::after {
      background-color: var(--second-clr);
    }

    &::after,
    &::before {
      content: '';
      position: absolute;
      left: 0;
    }

    &::before {
      bottom: 6px;
    }
    &::after {
      top: 6px;
    }
  }

  /* navbar--group */
  .navbar--group {
    transition: transform 350ms ease-in-out, opacity 200ms linear;
    position: absolute;
    top: 0;
    right: 0;
    background-color: var(--white-clr);
    height: 100vh;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-evenly;
    width: 70vw;
    transform: ${props =>
      props.toggle ? 'translateX(0)' : 'translateX(105%)'};
  }
`;

const Navbar = () => {
  const [toggle, setToggle] = useState(false);

  return (
    <Styled toggle={toggle}>
      <div className='head--top'>
        <Socials />
        <Link to='/'>
          <a className='brand-logo--top hide--md'>
            <img src='/logo.png' alt='logo' />
          </a>
        </Link>
        <div className='info'>
          <a className='info--link' href='#'>
            <FaPhone />
            (312) 666-8601
          </a>
          <a className='info--link' href='#'>
            <FaMapMarkerAlt />
            960 W 18th, Chicago, IL 60608
          </a>
        </div>
      </div>

      <nav className='navbar'>
        <Link to='/'>
          <a className='brand-logo hide--lg'>
            <img src='/logo.png' alt='logo' />
          </a>
        </Link>
        <div className='burger toggle' onClick={() => setToggle(!toggle)}></div>
        <ul className='navbar--group'>
          <li className='nav--link'>
            <Link to='/'>
              <a className='nav--link__item'> HOME</a>
            </Link>
          </li>
          <li className='nav--link'>
            <Link to='/about'>
              <a className='nav--link__item'>ABOUT</a>
            </Link>
          </li>
          <li className='nav--link'>
            <Link to='/menu'>
              <a className='nav--link__item'>MENU</a>
            </Link>
          </li>
          <li className='nav--link'>
            <Link to='/privateEvents'>
              <a className='nav--link__item'>PRIVATE EVENTS</a>
            </Link>
          </li>
          <li className='nav--link'>
            <Link to='/photoGallery'>
              <a className='nav--link__item'>PHOTO GALLERY</a>
            </Link>
          </li>
          <li className='nav--link'>
            <Link to='/press'>
              <a className='nav--link__item'> PRESS</a>
            </Link>
          </li>
          <li className='nav--link'>
            <Link to='/contact'>
              <a className='nav--link__item'>CONTACT</a>
            </Link>
          </li>
        </ul>
      </nav>
    </Styled>
  );
};

export default Navbar;
