import styled from 'styled-components';
import Link from 'next/link';
import Button from './button';

const Styled = styled.div`
  .title {
    font-family: var(--fn-monterrat);
    font-weight: var(--fw-bold);
    font-size: 4rem;
  }
`;

const Banner = ({ title, banner }) => {
  const single = (
    <>
      <h1 className='title'>{title}</h1>
      <img src='/whiteLine.png' className='whiteLine' />
    </>
  );

  const home = (
    <>
      <h1 className='title'>SIMONEâ€™S</h1>
      <h2 className='subtitle'>Bar & Grill in the heart of Pilsen</h2>
      <Button title='visit us' toLink={'/contact'} />
    </>
  );

  return <Styled>{banner === 'home' ? home : single}</Styled>;
};

export default Banner;
