import { useState, useRef, useEffect } from 'react';
import styled from 'styled-components';
import Arrow from './arrow';
import ImgSlider from './imgSlider';
import { FaArrowCircleLeft, FaArrowCircleRight } from 'react-icons/fa';

const Styled = styled.section`
  /* position: relative;
  width: 100%;
  height: 300px;
  margin: 5em 0;
  overflow-x: hidden;
  scroll-snap-points-x: repeat(300px);
  scroll-snap-type: mandatory;
  transform: scale(0.98); */

  -webkit-overflow-scrolling: touch;
  /* display: flex; */
  position: relative;
  height: 300px;
  width: 100%;
  overflow-x: scroll;
  overflow-y: hidden;
  white-space: nowrap;
  transition: all 10s linear;
  transform: scale(0.98);
  will-change: transform;
  user-select: none;
  cursor: pointer;

  .img--slider {
    /* flex-shrink: 0; */
    display: inline-block;
    height: 100%;
    width: 300px;
    margin: 2em 1em;
  }

  .slide-arrow {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    color: var(--white-clr);
    font-size: 2rem;
    margin: 0 0.2em;
    transition: var(--ease--in--out--02s);

    &:hover,
    &:focus {
      color: var(--second-clr);
    }
  }

  .right {
    right: 0;
  }
`;

const Carousel = () => {
  const carouselRef = useRef();
  const [isDown, setIsDown] = useState(false);
  const [startX, setStartX] = useState();
  const [scrollLeft, setScrollLeft] = useState();
  const [offsetLeft, setOffsetLeft] = useState();

  const imgs = {
    0: '/burger.jpg',
    1: '/craft_beer.jpg',
    2: '/drink_pink.jpg',
    3: '/SimonesBarFaceS.jpg',
    4: '/PinballBooth.jpg',
    5: '/pizza.jpg',
    6: '/patio.jpg',
    7: '/santi.jpg'
  };
  useEffect(() => {
    if (!isDown) return;
    const x =
      e.pageX - carouselRef.current.offsetLeft &&
      carouselRef.current.offsetLeft;
    const walk = (x - startX) * 3;
    carouselRef.current.scrollLeft = scrollLeft - walk;
  }, [isDown]);

  const handleMouseDown = e => {
    setIsDown(true);
    setStartX(e.pageX - carouselRef.current.offsetLeft);
    setScrollLeft(carouselRef.current.scrollLeft);
  };

  const handleMouseMove = e => {
    if (!isDown) return;
    const x =
      e.pageX - carouselRef.current.offsetLeft &&
      carouselRef.current.offsetLeft;
    const walk = (x - startX) * 3;
    carouselRef.current.scrollLeft = scrollLeft - walk;
  };
  const mouseOff = () => setIsDown(false);

  const mouseOn = e => {
    setIsDown(true);
    setStartX(e.pageX - carouselRef.current.offsetLeft);
  };

  console.log(isDown);
  console.log(carouselRef);

  return (
    <Styled
      className='carousel'
      ref={carouselRef}
      onMouseMove={mouseOn}
      onMouseDown={mouseOn}
      onMouseLeave={mouseOff}
      onMouseUp={mouseOff}
    >
      <Arrow direction='left' glyph={<FaArrowCircleLeft />} />
      <Arrow direction='right' glyph={<FaArrowCircleRight />} />
      {Object.values(imgs).map((img, i) => (
        <ImgSlider className='img--slider' key={i} imgUrl={img} />
      ))}
    </Styled>
  );
};

export default Carousel;
