import styled from 'styled-components';
import TitleBanner from '../ui/titleBanner';

const Styled = styled.section`
  text-align: center;
  display: grid;
  gap: 2em;

  .img--top,
  .img--bottom {
    display: none;
  }

  .quote {
    position: relative;

    &::before,
    &::after {
      content: '';
      font-size: 50px;
      height: 10px;
      width: 10px
      color: red;
      top: 0;
      right: 0;
      left: 0;
      position: absolute;
    }
  }

  .review--name {
    color: var(--accent-clr);
    font-family: var(--fn-reenie);
    font-size: 2.4rem;
    -webkit-text-stroke: 0.1px var(--accent-clr);
  }
`;

const UpcomingEvents = () => (
  <Styled className='upcoming--events section'>
    <TitleBanner
      topTitle='GET INVOLVED'
      title='UPCOMING EVENTS'
      botTitle='COME LIVE UP THE FUN'
    />
    <div className='container'>
      <img src='/dancing.jpg' alt='bar crowd' className='img--top' />
      <img src='/event.png' alt='event calender' className='event--calender' />
      <img src='/bar_crowd.jpg' alt='bar crowd' className='img--bottom' />
    </div>
    <div className='qoute'>
      <p className='review'>
        Came here on a friday night for the first time. I had such a good time.
        The drinks are great staff was very attentive. I especially loved the
        vegan burger i had. Very nice atmosphere. Definatly recommend this bar
        and will be back soon!!!
      </p>
      <h3 className='review--name'>Linda Colon Corona</h3>
    </div>
  </Styled>
);

export default UpcomingEvents;
