import { useState, useEffect } from 'react';
import Carousel from '@brainhubeu/react-carousel';
import styled from 'styled-components';

const Styled = styled.section`
  width: 100%;
  height: 300px;
  margin: 5em 0;
  overflow-x: hidden;

  ul {
    display: flex;
    li {
      flex-shrink: 0;
      height: 300px;

      img {
        height: 100%;
        object-fit: cover;
      }
    }
  }
`;

const Carousell = () => {
  const [windowSize, setWindowSize] = useState(0);

  const imgs = {
    0: '/burger.jpg',
    1: '/craft_beer.jpg',
    2: '/drink_pink.jpg',
    3: '/SimonesBarFaceS.jpg',
    4: '/PinballBooth.jpg',
    5: '/pizza.jpg',
    6: '/patio.jpg',
    7: '/santi.jpg'
  };

  useEffect(() => {
    setWindowSize(window.outerWidth);
  }, []);

  const size = () => {
    if (windowSize < 768) {
      return 1;
    } else if (windowSize >= 768) {
      return 3;
    } else if (windowSize <= 1250) {
      return 4;
    }
  };

  console.log('size', windowSize);
  return (
    <Styled className='carousel'>
      <Carousel
        slidesPerPage={size}
        infinite
        autoPlay={3000}
        animationSpeed={2000}
      >
        {Object.values(imgs).map((img, i) => (
          <img key={i} src={img} alt='img' />
        ))}
      </Carousel>
    </Styled>
  );
};

export default Carousell;
