import styled from 'styled-components';
import Button from './button';

const Styled = styled.div`
  position: relative;
  background: url('/outisde.jpg');
  background-repeat: no-repeat;
  background-position: center;
  background-attachment: scroll;
  background-size: cover;
  width: 100%;
  max-width: 500px;
  height: 600px;
  /* display: grid;
  justify-items: center; */
  text-align: center;

  .img--banner__img {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    height: 100%;
    width: 100%;
  }
`;

const ImgBanner = ({ img, title, subtile, btnTitle, toLink }) => (
  <Styled className='img--banner'>
    <div className='img--banner__img'>
      <img src='/box.png' alt='img banner' />
    </div>
    <h2 className='title'>{title}</h2>
    <h3 className='subtitle'>{subtile}</h3>
    <Button toLink={toLink} title={btnTitle} />
  </Styled>
);

export default ImgBanner;
