          >
            Soups&Salads
          </TabStyled>
          <TabStyled
            color
            onClick={() => setTab('pizzas')}
            active={tab === 'pizzas'}
          >
            pizzas
          </TabStyled>
          <TabStyled
            color
            onClick={() => setTab('sandwiches')}
            active={tab === 'sandwiches'}
          >
            sandwiches
          </TabStyled>
          <TabStyled
            color
            onClick={() => setTab('desserts')}
            active={tab === 'desserts'}
          >
            desserts
          </TabStyled>
          <TabStyled
            color
            onClick={() => setTab('brunch')}
            active={tab === 'brunch'}
          >
            brunch
          </TabStyled>
        </div>
        <div className='menu'>
          {menu[tab].map(({ title, text, price }, i) => (
            <div key={title + i} className='menu--item'>
              <div className='menu--item--text'>
                <h3 className='title'>{title}</h3>
                <p className='para'>{text}</p>
              </div>
              <p className='price'>${price}</p>
            </div>
          ))}
        </div>
        <div className='menu--img'>
          <img
            src='/fast-food-lunch-dinner-hamburger-2-transparent.png'
            alt='nacho beer image'
          />
        </div>
      </div>
    </Styled>
  );
};

export default MenuContent;
