import { useState } from 'react';
import styled from 'styled-components';
import Arrow from './arrow';
import ImgSlider from './imgSlider';
import { FaArrowCircleLeft, FaArrowCircleRight } from 'react-icons/fa';

const Styled = styled.section`
  display: flex;
  align-items: center;
`;

const Carousel = () => {
  const [num, setNum] = useState(0);

  const imgs = [
    { 0: '/burger.jpg' },
    { 1: '/craft_beer.jpg' },
    { 2: '/drink_pink.jpg' },
    { 3: '/SimonesBarFaceS.jpg' },
    { 4: '/PinballBooth.jpg' },
    { 5: '/pizza.jpg' },
    { 6: '/patio.jpg' },
    { 7: '/santi.jpg' }
  ];

  console.log(imgs);
  return (
    <Styled className='carousel'>
      <Arrow direction='left' glyph={<FaArrowCircleLeft />} />
      {/* {imgs.map((img, i) => (
        <ImgSlider key={i} imgUrl={img} />
      ))} */}
      <Arrow direction='right' glyph={<FaArrowCircleRight />} />
    </Styled>
  );
};

export default Carousel;
