import { useState } from 'react';
import styled from 'styled-components';
import Arrow from './arrow';
import ImgSlider from './imgSlider';
import { FaArrowCircleLeft, FaArrowCircleRight } from 'react-icons/fa';

const Styled = styled.section`
  position: relative;
  width: 100%;
  height: 300px;

  .carousel--wrapper {
    height: inherit;
    display: flex;
    overflow-x: auto;
    -webkit-overflow-scrolling: touch;
    scroll-snap-points-x: repeat(300px);
    scroll-snap-type: mandatory;
  }

  .img--slider {
    flex-shrink: 0;
    width: 300px;
  }

  .slide-arrow svg {
    position: absolute;
    top: 50%;
    color: red;
    font-size: 2rem;
  }
`;

const Carousel = () => {
  const [num, setNum] = useState(0);

  const imgs = {
    0: '/burger.jpg',
    1: '/craft_beer.jpg',
    2: '/drink_pink.jpg',
    3: '/SimonesBarFaceS.jpg',
    4: '/PinballBooth.jpg',
    5: '/pizza.jpg',
    6: '/patio.jpg',
    7: '/santi.jpg'
  };

  console.log(Object.values(imgs));
  return (
    <Styled className='carousel'>
      <Arrow direction='left' glyph={<FaArrowCircleLeft />} />
      <Arrow direction='right' glyph={<FaArrowCircleRight />} />
      <div className='carousel--wrapper'>
        {Object.values(imgs).map((img, i) => (
          <ImgSlider className='img--slider' key={i} imgUrl={img} />
        ))}
      </div>
    </Styled>
  );
};

export default Carousel;
